import java.util.*;

public class Solution {
    // 节点的基本构造
    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    // 递归前序遍历
    public static void preOrderRecur(Node head) {
        if (head == null) {
            return;
        }
        System.out.print(head.value + " ");
        preOrderRecur(head.left);
        preOrderRecur(head.right);
    }

    // 非递归前序遍历
    public static void preOrderUnRecur(Node head) {
        System.out.print("pre-order: ");
        if (head != null) {
            // 采用栈结构
            Stack<Node> stack = new Stack<Node>();
            stack.add(head);
            while (!stack.isEmpty()) {
                // 这里的head是复用的，表示当前节点
                head = stack.pop();
                System.out.print(head.value + " ");
                // 整体的顺序是先压右后压左
                if (head.right != null) {
                    stack.push(head.right);
                }
                if (head.left != null) {
                    stack.push(head.left);
                }
            }
        }
        System.out.println();
    }
}

import java.util.*;

public class Solution {
    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    //递归
    public static void inOrderRecur(Node head) {
        if (head == null) {
            return;
        }
        inOrderRecur(head.left);
        System.out.print(head.value + " ");
        inOrderRecur(head.right);
    }

    //非递归
    public static void inOrderUnRecur(Node head) {
        System.out.print("in-order: ");
        if (head != null) {
            Stack<Node> stack = new Stack<Node>();//左根右
            //！stack.isEmpty这个条件只是用来判断是否结束而已，关键的是head != null这个条件
            while (!stack.isEmpty() || head != null) {
                //如果当前节点不为空将当前节点压入栈然后当前节点往左;当前节点为空从栈拿一个打印，然后当前节点往右边跑
                if (head != null) {
                    stack.push(head);
                    head = head.left;
                } else {
                    //把当前节点指向了从栈弹出的那个节点
                    head = stack.pop();
                    System.out.print(head.value + " ");
                    head = head.right;
                }
            }
        }
        System.out.println();
    }
}

import java.util.*;

public class Solution {
    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    public static void inOrderRecur(Node head) {
        if (head == null) {
            return;
        }
        inOrderRecur(head.left);
        inOrderRecur(head.right);
        System.out.print(head.value + " ");
    }

    public static void posOrderUnRecur1(Node head) {
        System.out.print("pos-order: ");
        if (head != null) {
            Stack<Node> s1 = new Stack<Node>();
            Stack<Node> s2 = new Stack<Node>();
            s1.push(head);
            while (!s1.isEmpty()) {
                head = s1.pop();
                s2.push(head);
                //本来是打印的时机，但你先不打印全部先放到辅助栈s2
                if (head.left != null) {
                    s1.push(head.left);
                }
                if (head.right != null) {
                    s1.push(head.right);
                }
            }
            //再打印栈s2
            while (!s2.isEmpty()) {
                System.out.print(s2.pop().value + " ");
            }
        }
        System.out.println();
    }
}

public class Solution {
    public static class Node {
        int  value;
        Node left;
        Node right;

        public Node(int data){
            this.value = data;
        }
    }

    public static void levelRead(Node head) {
        // 当结点非空时才进行操作
        if (head != null) {
            // 用于存放还未遍历的元素
            LinkedList<Node> queue = new LinkedList<>();
            // 将根结点入队
            queue.add(head);
            // 用于记录当前处理的结点
            //Node head;

            // 队列非空则进行处理
            while (!queue.isEmpty()) {
                // remove()表示删除队首元素
                head = queue.remove();
                // 输出队首元素的值
                System.out.print(head.value + " ");
                // 如果左子结点不为空，则左子结点入队
                if (head.left != null) {
                    queue.add(head.left);
                }
                // 如果右子结点不为空，则左子结点入队
                if (head.right != null) {
                    queue.add(head.right);
                }
            }
        }
    }
}

/*
 * 判断一颗二叉树是否是平衡二叉树，它是一棵空树或任意节点左右两个子树的高度差的绝对值不超过1称为平衡二叉树
 *
 * 这道题目充分体现了递归对于解决树相关算法题的作用,如果我们抛开先序中序后序的打印时机,其实递归来到每个节点的次数是三次
 * 我既然可以回到要给节点三次,我们就可以收集左子树和右子树的信息,最后回到根节点,进行判断,可以总结为一个高度解题套路
 * 譬如我为了判断X为根节点的树是不是平衡树,我们可以先判断节点X的左树是否平衡,右树是否平衡,然后获取左树高度和右树高度比较是否超过1
 * 套路就是要设计我们的递归返回结构,对于这道题来说我们需要递归函数给我们返回信息包括了这棵树是否平衡和这棵树高度;
 */

public class Solution {

    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    // 设计递归的返回结构
    public static class ReturnData{
        // 是否是平衡二叉树
        public boolean isBalance;
        // 树的高度
        public int height;

        public ReturnData(boolean isBalance,int height){
            this.isBalance = isBalance;
            this.height = height;
        }
    }

    public static boolean isBalanceTree(Node head) {
        return process(head).isBalance;
    }

    // 设计递归函数,返回信息包括该树是否是平衡二叉树以及该树的高度
    public static ReturnData process(Node head){
        // 如果是空树认为也算是平衡二叉树
        if(head == null){
            return new ReturnData(true,0);
        }
        ReturnData leftData = process(head.left);
        // 如果左树已经是不平衡直接返回不平衡
        if(!leftData.isBalance){
            return new ReturnData(false,0);
        }
        ReturnData rightData = process(head.right);
        // 如果右树已经是不平衡直接返回不平衡
        if(!rightData.isBalance){
            return new ReturnData(false,0);
        }
        // 说明左树和右树都已经平衡了
        if(Math.abs(leftData.height - rightData.height) > 1){
            return new ReturnData(false,0);
        }
        // return在递归中很重要,左树和右树高度较大的那个加1就是我自己的高度
        return new ReturnData(true,Math.max(leftData.height , rightData.height) + 1);
    }

}

import java.util.*;

/* 解题规律:
 * 1.首先我们要明白后继节点值得是,在二叉树终须遍历序列中,node的下一个节点叫做node的后继节点
 * 2.如果节点X有右子树,那么该节点的后继节点一定是其右子树最左的节点(结合中序遍历思考得出);
 * 3.如果节点X没有右子树，通过节点X的Parent指针找节点X的父节点,一直往上找，直到节点X是该某个节点的左孩子，那么这个某个节点就是X节点的后继节点
 */
public class Solution {
    public static class Node {
        public int value;
        public Node left;
        public Node right;
        public Node parent;

        public Node(int data) {
            this.value = data;
        }
    }

    public static Node getSuccessorNode(Node node) {
        if (node == null) {
            return node;
        }

        if (node.right != null) {
            //如果一个节点有右子树,直接找到这颗右子树上最左的节点
            return getLeftMost(node.right);
        } else {
            Node parent = node.parent;
            //parent != null处理边界值，parent.left != node当前节点等于自己的左孩子停
            while (parent != null && parent.left != node) {
                node = parent;
                parent = node.parent;
            }
            return parent;
        }
    }

    //拿到右子树上最左的节点
    public static Node getLeftMost(Node node) {
        if (node == null) {
            return node;
        }
        while (node.left != null) {
            node = node.left;
        }
        return node;
    }
}

import java.util.*;

/*
 * 判断一棵树是否是搜索二叉树，左子树值比自己小，右子树值比自己大。其实只要满足二叉树中序遍历结果是依次升序的就是搜索二叉树
 */

public class Solution {

    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    public static boolean inOrderUnRecur(Node head) {
        if (head != null) {
            int pre = 0;
            Stack<Node> stack = new Stack<Node>();
            while(!stack.isEmpty() || head != null) {
                if (head != null) {
                    stack.push(head);
                    head = head.left;
                }else {
                    stack.pop();
                    // 当前这个数是否比前一个数小则不满足搜索二叉树
                    if (head.value < pre){
                        return false;
                    }
                    head = head.right;
                }
            }
            return true;
        }
    }
}

import java.util.*;

/* 如何判断一个数是否是完全二叉树,按层一个节点一个节点遍历,每个节点需要满足下面两个条件
 * 第一阶段：如果一个节点只有右孩子没有左孩子绝对不是完全二叉树,可以直接返回false
 * 第二阶段：如果一个节点不是左右两个孩子都全(那么这里要么是有左孩子没有右孩子要么是左右都没有孩子)，
 *         那么在这样的情况下按层遍历如果未遍历的节点都是叶子节点则说明是完全二叉树
 */

public class Solution {
    public static class Node {
        public int value;
        public Node left;
        public Node right;

        public Node(int data) {
            this.value = data;
        }
    }

    public static boolean isCompleteBinaryTree(Node head) {
        if (head == null) {
            return true;
        }
        Queue<Node> queue = new LinkedList<Node>();
        //标志是否进入了第二阶段，开启后续节点都是叶节点的这样一个阶段
        boolean leaf = false;
        Node l = null;
        Node r = null;
        queue.offer(head);
        while (!queue.isEmpty()) {
            head = queue.poll();
            l = head.left;
            r = head.right;
            //这个if语句中有两个判断条件：
            //第一个条件：如果我开启了第二阶段了 并且左孩子不为空或者右孩子不为空 (标准二)
            //第二个条件：左孩子为空，右孩子也不为空(标准一)
            if ((leaf && (l != null || r != null))
                    ||
                    (l == null && r != null)){
                return false;
            }
            if (l != null) {
                queue.offer(l);
            }
            if (r != null) {
                queue.offer(r);
            }
            if (l == null || r == null) {
                //进入第二阶段
                leaf = true;
            }
        }
        return true;
    }
}

public class Solution {
    public static void bubbleSort(int[] arr){
        if(arr == null || arr.length < 2){
            return;
        }

        for (int end = arr.length -1; end > 0; end--){
            //设置这一趟并未发生交换
            boolean exchange = false;
            for (int i = 0; i < end; i++){
                if (arr[i] > arr[i+1]){
                    swap(arr, i, i + 1);
                    //已经发生交换
                    exchange = true;
                }
            }
            //如果没有发生交换则break说明前面都已经排序完毕，节省没必要的比较
            if (!exchange) break;
        }
    }

    public static void swap(int[] arr, int i, int j){
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
}

public class Solution {
    public static void selectionSort(int[] arr){
        if(arr == null || arr.length < 2){
            return;
        }
        //i < length - 1数组的最后一位没有必要再进行比较
        for (int i = 0; i < arr.length - 1; i++){
            //我定义的最小值的Index
            int minIndex = i;
            //这里就可以说j对于i是不变的，虽然i是变的；j其实是每一趟的起始值
            for (int j = i + 1;j < arr.length;j++){
                //实际找到的minIndex
                minIndex = arr[j] < arr[minIndex] ? j:minIndex;
            }
            swap(arr, i, minIndex);
        }
    }
    //交换数组中两个元素
    public static void swap(int[] arr, int i, int j){
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
    //打印数组的每一个元素出来
    public static void printArray(int[] arr) {
        if (arr == null) {
            return;
        }
        for (int i = 0; i < arr.length; i++) {
            System.out.print(arr[i] + " ");//这里加空格
        }
        //下面这句总体换一行，细节真的很关键
        System.out.println();
    }
}

public class Solution {
    public static void insertSort(int[] arr) {
        if (arr == null || arr.length < 2) {
            return;
        }
        for (int i = 1; i < arr.length; i++) {
            //就像打扑克牌排序一样插入排序，一张牌来了，从右往左遍历
            for (int j = i - 1; j >= 0 && arr[j] > arr[j + 1]; j--) {
                swap(arr, j, j + 1);
            }
        }
    }

    //交换数组中两个元素
    public static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
}

public class Solution {
    public static void mergeSort(int[] arr){
        if (arr == null || arr.length < 2){
            return;
        }
        mergeSort(arr, 0, arr.length - 1);
    }

    public static void mergeSort(int[] arr, int l,int r){
        if(l == r){
            return;
        }
        int mid = l + ((r - l) >> 1);
        mergeSort(arr, l, mid);
        mergeSort(arr, mid+1, r);
        merge(arr, l, mid, r);
    }

    public static void merge(int[] arr, int l, int m, int r){
        int[] help = new int[r - l + 1];
        int i = 0;
        int p1 = l;
        int p2 = m + 1;
        while (p1 <= m && p2 <= r){
            help[i++] = arr[p1] < arr[p2] ? arr[p1++] : arr[p2++];
        }
        while (p1 <= m){
            help[i++] = arr[p1++];
        }
        while (p2 <= r){
            help[i++] = arr[p2++];
        }

        for(i = 0; i < help.length; i++){
            arr[l + i] = help[i];
        }
    }
}

public class Solution {
    public static void quickSort(int[] arr) {
        if (arr == null || arr.length < 2) {
            return;
        }
        quickSort(arr, 0, arr.length - 1);
    }

    public static void quickSort(int[] arr, int L, int R) {
        //这个条件很重要,整个过程必须要在L<R下进行
        if (L < R) {
            //随机选择一个数和最后一个数交换，然后采用最后一位数做标志位
            swap(arr, L + (int) (Math.random() * (R - L + 1)), R);
            int[] p = partition(arr, L, R);
            //递归遍历,每次确定中间的一片数字
            quickSort(arr, L, p[0] - 1);
            quickSort(arr, p[1] + 1, R);
        }
    }

    public static int[] partition(int[] arr, int L, int R) {
        //数组第零位的前面一位,这里复用了L变量而已
        int less = L - 1;
        //把数组的最后一位当成了Partition问题中的比较数字
        int more = R;

        while (L < more) {
            if (arr[L] < arr[R]) {
                swap(arr, ++less, L++);
            } else if (arr[L] > arr[R]) {
                swap(arr, --more, L);
            } else {
                L++;
            }
        }
        //因为最后一位并没有参与遍历此时数组的划分为 <X | =X | >X | X 四个区域
        //为此我们要通过swap函数交换为 <X | =X | >X 三个区域
        swap(arr, more, R);
        //返回等于区域相应的下标
        return new int[] { less + 1, more };
    }

    public static void swap(int[] arr, int i, int j) {
        int tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }
}

import java.util.Arrays;


public class Solution {

    public static void heapSort(int[] arr) {
        if (arr == null || arr.length < 2) {
            return;
        }
        // 下面这一步后整个数组就是大根堆结构了
        for (int i = 0; i < arr.length; i++) {
            // 依次把i位置上的数加进来，让他从0-i位置上称为大根堆
            heapInsert(arr, i);
        }
        int heapSize = arr.length;

        // 将堆顶与堆底最后一个数字交换
        swap(arr, 0, --heapSize);

        while (heapSize > 0) {
            // 每次都修正整个堆，让堆顶是最大的数字
            heapify(arr, 0, heapSize);
            // 将堆顶与heapSize最后一个数交换,每次交换都缩小heapSize的范围，将已经排序完毕的数字排出去
            swap(arr, 0, --heapSize);
        }
    }

    //建立大根堆,把数组变为大根堆结构,index表示即将要加进来的节点
    public static void heapInsert(int[] arr, int index) {
        // while循环将子节点比父节点大的节点提上来
        while (arr[index] > arr[(index - 1) / 2]) {
            // 当前我比我的父位置要大，则交换
            swap(arr, index, (index - 1) / 2);
            // 交换完之后继续与父位置进行比较直到满足大根堆
            // 此处精妙之处在于当节点一直跳到最顶的节点那么会变成arr[0]与arr[(0-1)/2]比较，则必定相等跳出循环
            index = (index - 1) / 2;
        }
    }

    // 修正数组为堆结构,这个函数的意思是在0 ~ heapSize-1我认为形成了堆，除了这个范围我认为越界；
    // index的值发生了变小了，导致index这个节点应该往下沉
    // heapSize的意思是整个堆上有多少个数，与数组个数并没有关系，但一定不会比数组个数大；
    public static void heapify(int[] arr, int index, int heapSize) {
        int left = index * 2 + 1;
        //while成立条件是我的左还是还在堆上
        while (left < heapSize) {
            //找出左右节点最大值的下标，left + 1表示右孩子;只有你有右孩子并且右孩子比左孩子要大那么largest才是右孩子
            int largest = left + 1 < heapSize && arr[left + 1] > arr[left]
                    ? left + 1
                    : left;
            //找到左右节点最大值与父节点比较找到最大值下标，并将其作为largest
            largest = arr[largest] > arr[index] ? largest : index;
            //如果发现最大值是自己则不需要交换break
            if (largest == index) {
                break;
            }
            //largest != index
            swap(arr, largest, index);
            index = largest;
            //left又往下走重复整个while循环
            left = index * 2 + 1;
        }
    }

    public static void swap(int[] arr, int i, int j) {
        int tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }
}

public class Solution {
    public static void niXuDui(int[] arr) {
        if(arr == null || arr.length < 2)
            return;

        mergeSort(arr, 0, arr.length - 1);
    }

    public static void mergeSort(int[] arr, int L, int R) {
        if(L == R) {
            return;
        }

        int mid = L + (R - L) / 2;
        mergeSort(arr, L, mid);
        mergeSort(arr, mid + 1, R);
        merge(arr, L, mid, R);
    }

    public static void merge(int[] arr, int L, int m, int R) {
        int[] help = new int[R - L + 1];
        int i = 0;
        int p1 = L;
        int p2 = m + 1;

        while (p1 <= m && p2 <= R) {
            // 如果这个条件成立，那么左半部分的所有数一定大于右半部分
            if(arr[p1] > arr[p2]) {
                for(int j = p1; j <= m; j++) {
                    System.out.println(arr[j] + ", " + arr[p2]);
                }
            }
            help[i++] = arr[p1] < arr[p2] ? arr[p1++] : arr[p2++];
        }

        while (p1 <= m) {
            help[i++] = arr[p1++];
        }

        while (p2 <= R) {
            help[i++] = arr[p2++];
        }

        for(i = 0; i < help.length; i++) {
            arr[L + i] = help[i];
        }
    }

    public static void main(String[] args) {
        int[] a = {1, 3, 4, 2, 5};
        niXuDui(a);

    }
}
